import React, { useState,createContext } from "react";
import Header from "./Header";
import Balance from "./Balance";
import IncomeExpenses from "./IncomeExpenses";
import TransactionList from "./TransactionList";
import AddTransaction from "./AddTransaction";










const tp=createContext(); 

function App(){

   const [transactions,setTransactions]=useState( [
    { id: 1, text: 'Flower', amount: -20 },
    { id: 2, text: 'Salary', amount: 300 },
    { id: 3, text: 'Book', amount: -10 },
    { id: 4, text: 'Camera', amount: 150 }
  ])
    

  function deleteTransaction(id1){
  
       return setTransactions((pre)=>{
        return  pre.filter((trans)=>(trans.id !== id1))
       })
       
  }


  function addTransaction({text,amount}){

    return setTransactions((pre)=>{
        return [...pre,{text,amount}]
    })
  }








    return (
<div>
<tp.Provider value={{transactions,deleteTransaction,addTransaction}}>
<Header/>

<div className="container">
<Balance/>
<IncomeExpenses/>
<TransactionList  />
<AddTransaction  />
</div>
    
</tp.Provider>




</div>

    )
}


export default App;

export {tp};
