import React from 'react'
import { tp } from './App'


function Balance() {
  const balance=useContext(tp)
  const amounts = balance.map(transaction => transaction.amount);

  const total = amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);

  
console.log(total)
  return (
    <div> 
    <h4>Your Balance</h4>
    <h1 id="balance">0.00</h1>
    </div>
  )
}


export default Balance
