import React, { useContext } from 'react'
import { tp } from './App'

function TransactionList(props){

  const tl=useContext(tp)
  console.log(tl)

  const sign = tran.amount < 0 ? '-' : '+';
  return (
    <div>
    <h3>History</h3>
      <ul id="list" className="list">

      {tl.map((tran)=>{
        return  <li className="minus">
          {tran.text}<span>{tran.amount}</span><button className="delete-btn">x</button>
        </li> 
      })}

      
      </ul>
    </div>
  )
}




export default TransactionList
