import React,{useContext} from 'react'
import { tp } from './App'


function Balance() {
  const {transactions:balance}=useContext(tp)
  const amounts = balance.map(transaction => transaction.amount);

  const total = amoun.reduce((acc, item) => (acc += item), 0).toFixed(2);

  

  return (
    <div> 
    <h4>Your Balance</h4>
    <h1 id="balance">{total}</h1>
    </div>
  )
}


export default Balance
